package JavaSkills;

import java.util.Scanner;

public class AS_4_Taking_Input {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	Scanner s = new Scanner(System.in);
	 int a = s.nextInt();
	 int b = s.nextInt();
	 int sum = a+b;
	 System.out.println(sum);
	 
	 
	 
	}

}